func TestABIDiff(t *testing.T) {

	currentABI, err := abi.JSON(strings.NewReader(contracts.BaseContentABI))
	require.NoError(t, err)

	testABI, err := abi.JSON(strings.NewReader(TestBaseContentABI))
	require.NoError(t, err)

	diffs, err := diffABIs("BaseContentABI", currentABI, testABI)
	require.NoError(t, err)
	require.True(t, len(diffs) > 0)

	cntBreaking := 0
	for _, diff := range diffs {
		if diff.Breaking {
			cntBreaking++
			println(diff.Report)
		}
	}
	require.True(t, cntBreaking > 0)

	diffs, err = diffABIs("BaseContentABI", currentABI, currentABI)
	require.NoError(t, err)
	require.True(t, len(diffs) == 0)
}